const sha256 = require('js-sha256');

function bigint_to_array(n, k, x) {
  let mod = 1n;
  for (var idx = 0; idx < n; idx++) {
    mod = mod * 2n;
  }

  let ret = [];
  var x_temp = x;
  console.log("x_temp original: ", x_temp);
  for (var idx = 0; idx < k; idx++) {
    ret.push(x_temp % mod);
    x_temp = x_temp / mod;
    console.log("x_temp every time: ", x_temp);
  }
  return ret;
}

